<?php 	include(dirname(dirname(dirname(__FILE__))).'/objects/class_connection.php');	include(dirname(dirname(dirname(__FILE__))).'/objects/class_promo_code.php');		$database=new cleanto_db();	$conn=$database->connect();	$database->conn=$conn;	$promo=new cleanto_promo_code();	$promo->conn=$conn;		$alldata=$promo->readall_service();	/* Code for Add */if(isset($_POST['action']) && $_POST['action']=='add_promo_code'){    $promo->coupon_code = $_POST['coupon_code'];    $t = $promo->check_same_title();    $cnt = mysqli_num_rows($t);    if($cnt == 0) {        $promo->coupon_code = $_POST['coupon_code'];        $promo->coupon_type = $_POST['coupon_type'];        $promo->value = $_POST['value'];        $promo->limit_use = $_POST['limit'];        $promo->expiry_date = $_POST['expiry_date'];        $promo->add_promo_code();    }    else{        echo "1";    }}if(isset($_POST['action']) && $_POST['action']=='edit_promo_code'){	    $promo->id=$_POST['recordid'];        $promo->coupon_code=$_POST['edit_coupon_code'];        $promo->coupon_type=$_POST['edit_coupon_type'];        $promo->value=$_POST['edit_value'];        $promo->limit_use=$_POST['edit_limit'];        $promo->expiry_date=$_POST['edit_expiry_date'];        $savedata=$promo->update_promo_code();        if($savedata){            echo "Data Updated";        }else{            echo "Data Not Updated";        }}if(isset($_POST['action']) && $_POST['action']=='delete_record'){		$promo->id=$_POST['recordid'];	$delete=$promo->delete_promo_code();	if($delete){		echo "Record Deleted";	}else{		echo "Record Not Deleted";	}}